import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const RaffleDetails = ({ raffle }) => {
  const navigate = useNavigate();
  const [isSelecting, setIsSelecting] = useState(false);
  const [currentParticipant, setCurrentParticipant] = useState('');
  const [winner, setWinner] = useState(null);
  const [error, setError] = useState(null);

  const handleEndRaffle = async () => {
    if (isSelecting) return;
    setIsSelecting(true);
    setError(null);

    // Simulate cycling through participants
    const participants = raffle.participants || [];
    if (participants.length === 0) {
      setError('No participants in this raffle');
      setIsSelecting(false);
      return;
    }

    // Animation: Cycle through participant names
    let cycleCount = 0;
    const maxCycles = 10; // Adjust for animation duration
    const cycleInterval = setInterval(() => {
      const randomIndex = Math.floor(Math.random() * participants.length);
      setCurrentParticipant(participants[randomIndex]?.displayName || 'Selecting...');
      cycleCount++;
      if (cycleCount >= maxCycles) {
        clearInterval(cycleInterval);
      }
    }, 300); // Adjust speed of cycling

    // Call backend after animation
    setTimeout(async () => {
      try {
        const token = localStorage.getItem('adminToken');
        const response = await axios.post(
          `http://localhost:5000/api/raffles/end/${raffle._id}/${raffle.creatorSecret}`,
          {},
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setWinner(response.data.winner || 'No winner selected');
        setIsSelecting(false);
      } catch (err) {
        console.error('End raffle error:', err);
        setError('Failed to end raffle: ' + err.response?.data?.error || err.message);
        setIsSelecting(false);
      }
    }, maxCycles * 300); // Match animation duration
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold">{raffle.title}</h1>
      <p>{raffle.description}</p>
      <p>Cash Prize: {raffle.cashPrize}</p>
      <p>Ticket Price: {raffle.ticketPrice}</p>
      <p>End Time: {new Date(raffle.endTime).toLocaleString()}</p>
      <p>Participants: {raffle.participants?.length || 0}</p>
      <AnimatePresence>
        <div>
          {isSelecting && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0 }}
              className="mt-6 p-4 bg-gray-800 text-white rounded-lg text-center"
            >
              <h2 className="text-xl font-semibold">Selecting Winner...</h2>
              <motion.p
                key={currentParticipant}
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.3 }}
                className="text-2xl mt-2"
              >
                {currentParticipant}
              </motion.p>
            </motion.div>
          )}
          {winner && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, ease: 'easeOut' }}
              className="mt-6 p-4 bg-green-600 text-white rounded-lg text-center"
            >
              <h2 className="text-xl font-semibold">Winner!</h2>
              <p className="text-2xl mt-2">{winner}</p>
            </motion.div>
          )}
          {error && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="mt-6 p-4 bg-red-600 text-white rounded-lg text-center"
            >
              {error}
            </motion.div>
          )}
        </div>
        {!raffle.winner && !winner && (
          <>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleEndRaffle}
              disabled={isSelecting}
              className={`bg-[#FFD93D] text-black p-3 rounded-lg w-full font-semibold hover:bg-[#FFCA28] transition-colors mt-6 ${
                isSelecting ? 'opacity-50 cursor-not-allowed' : ''
              }`}
            >
              End Raffle & Pick Winner
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => navigate('/admin')}
              className="bg-gray-600 text-white p-3 rounded-lg w-full font-semibold hover:bg-gray-700 transition-colors mt-4"
            >
              Go back to admin dashboard
            </motion.button>
          </>
        )}
      </AnimatePresence>
    </div>
  );
};

export default RaffleDetails;
