import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { QRCodeCanvas } from 'qrcode.react'; // Updated import
import { motion } from 'framer-motion';
import ConfettiBurst from '../components/ConfettiBurst';

const TicketDownload = () => {
  const { id } = useParams();
  const [ticketData, setTicketData] = useState(null);
  const [showConfetti, setShowConfetti] = useState(true);

  useEffect(() => {
    // Fetch ticket details (mocked here, adjust based on backend response)
    axios.get(`http://localhost:5000/api/raffles/${id}`).then((res) => {
      setTicketData(res.data); // Assume includes participant data
    });
  }, [id]);

  const handleDownload = () => {
    axios({
      url: `http://localhost:5000/api/raffles/${id}/ticket`, // Adjust to backend endpoint
      method: 'GET',
      responseType: 'blob',
    }).then((response) => {
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'raffle-ticket.pdf');
      document.body.appendChild(link);
      link.click();
      link.remove();
    });
  };

  if (!ticketData) return <div>Loading...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#FF6B6B] to-[#FFD93D] flex items-center justify-center p-4">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-8 max-w-md w-full text-center"
      >
        <h1 className="text-3xl font-poppins font-bold text-[#4D96FF] mb-4">
          ðŸŽ‰ Congratulations!
        </h1>
        <p className="text-lg text-gray-700 dark:text-gray-200">
          You've secured your ticket for <strong>{ticketData.title}</strong>!
        </p>
        <div className="my-6">
          <QRCodeCanvas
            value={`raffle:${id},ticket:${ticketData.participants[ticketData.participants.length - 1]?.ticketNumber}`}
            size={150}
            className="mx-auto"
          />
          <p className="mt-2 text-[#FF6B6B]">
            Ticket Number: {ticketData.participants[ticketData.participants.length - 1]?.ticketNumber}
          </p>
          <p className="text-gray-600 dark:text-gray-200">
            Name: {ticketData.participants[ticketData.participants.length - 1]?.displayName}
          </p>
          <p className="text-gray-600 dark:text-gray-200">
            Expires: {new Date(ticketData.endTime).toLocaleString()}
          </p>
        </div>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleDownload}
          className="bg-[#6BCB77] text-white p-3 rounded font-semibold"
        >
          Download Ticket PDF
        </motion.button>
        <ConfettiBurst trigger={showConfetti} />
      </motion.div>
    </div>
  );
};

export default TicketDownload;
