import { useParams, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { PaystackButton } from 'react-paystack';
import CountdownTimer from '../components/CountdownTimer';
import { motion } from 'framer-motion';

const RaffleDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [raffle, setRaffle] = useState(null);
  const [name, setName] = useState('');
  const [contact, setContact] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRaffle = async () => {
      try {
        console.log(`Fetching raffle with ID: ${id}`);
        const response = await axios.get(`http://localhost:5000/api/raffles/${id}`);
        console.log('Raffle data:', response.data);
        setRaffle(response.data);
      } catch (err) {
        console.error('Error fetching raffle:', err.response?.data || err.message);
        setError(`Failed to load raffle: ${err.response?.data?.error || err.message}`);
      }
    };
    fetchRaffle();
  }, [id]);

  const publicKey = process.env.REACT_APP_PAYSTACK_PUBLIC_KEY || 'pk_test_9dd674c3ff8c8ed03348c35c8eb0da14b068fd4d';
  const componentProps = {
    email,
    amount: raffle?.ticketPrice * 100,
    currency: 'GHS',
    metadata: { name, contact, raffleId: id, referrer: window.location.href },
    publicKey,
    text: 'Join Now',
    onSuccess: (response) => {
      console.log('Paystack success:', response);
      axios
        .post('http://localhost:5000/api/raffles/verify-payment', {
          reference: response.reference,
          raffleId: id,
          name,
          contact,
        }, { responseType: 'blob' })
        .then((res) => {
          const url = window.URL.createObjectURL(new Blob([res.data], { type: 'application/pdf' }));
          const link = document.createElement('a');
          link.href = url;
          link.setAttribute('download', 'raffle-ticket.pdf');
          document.body.appendChild(link);
          link.click();
          document.body.removeChild(link);
          window.URL.revokeObjectURL(url);
          // Use ticketNumber from response headers or generate a temporary one
          const ticketNumber = res.headers['x-ticket-number'] || `TEMP-${Math.random().toString(36).substring(2, 10).toUpperCase()}`;
          navigate(`/ticket/${id}`, {
            state: {
              raffle,
              participant: { displayName: name, contact, ticketNumber }
            }
          });
        })
        .catch((err) => setError(`Payment verification failed: ${err.response?.data?.error || err.message}`));
    },
    onClose: () => setError('Payment cancelled'),
  };

  if (error) return <div className="text-red-500 text-center">{error}</div>;
  if (!raffle) return <div>Loading...</div>;

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="p-8 bg-white rounded-lg shadow-lg max-w-md mx-auto"
    >
      {raffle.prizeImage && <img src={raffle.prizeImage} alt="Prize" className="w-full h-64 object-cover rounded mb-4" />}
      <h1 className="text-3xl font-poppins font-bold text-[#FF6B6B] mb-4">{raffle.title}</h1>
      <p>{raffle.description}</p>
      <p>Cash Prize: GHS {raffle.cashPrize}</p>
      <p>Ticket Price: GHS {raffle.ticketPrice}</p>
      <p>Ends: {new Date(raffle.endTime).toLocaleString()}</p>
      <CountdownTimer endTime={raffle.endTime} />
      <div className="mt-4">
        <input
          placeholder="Display Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="border p-2 w-full mb-2"
          required
        />
        <input
          placeholder="Contact Number"
          value={contact}
          onChange={(e) => setContact(e.target.value)}
          className="border p-2 w-full mb-2"
          required
        />
        <input
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="border p-2 w-full mb-2"
          required
        />
        {raffle.ticketPrice > 0 ? (
          <PaystackButton {...componentProps} className="bg-[#4D96FF] text-white p-4 rounded w-full" />
        ) : (
          <button
            onClick={() =>
              axios
                .post('http://localhost:5000/api/raffles/init-payment', { raffleId: id, displayName: name, contact }, { responseType: 'blob' })
                .then((res) => {
                  const url = window.URL.createObjectURL(new Blob([res.data], { type: 'application/pdf' }));
                  const link = document.createElement('a');
                  link.href = url;
                  link.setAttribute('download', 'raffle-ticket.pdf');
                  document.body.appendChild(link);
                  link.click();
                  document.body.removeChild(link);
                  window.URL.revokeObjectURL(url);
                  const ticketNumber = res.headers['x-ticket-number'] || `TEMP-${Math.random().toString(36).substring(2, 10).toUpperCase()}`;
                  navigate(`/ticket/${id}`, {
                    state: {
                      raffle,
                      participant: { displayName: name, contact, ticketNumber }
                    }
                  });
                })
                .catch((err) => setError(`Failed to join: ${err.response?.data?.error || err.message}`))
            }
            className="bg-[#4D96FF] text-white p-4 rounded w-full"
          >
            Join Free
          </button>
        )}
      </div>
      <ul className="mt-4">
        {raffle.participants.map((p) => (
          <li key={p.ticketNumber}>{p.displayName}</li>
        ))}
      </ul>
    </motion.div>
  );
};

export default RaffleDetails;
