import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from '../components/CountdownTimer';
import { motion, AnimatePresence } from 'framer-motion';
import ConfettiBurst from '../components/ConfettiBurst';

const AdminDashboard = () => {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const secret = new URLSearchParams(location.search).get('secret');
  const [raffle, setRaffle] = useState(null);
  const [error, setError] = useState('');
  const [showConfetti, setShowConfetti] = useState(true);

  useEffect(() => {
    // Check admin authentication
    if (!localStorage.getItem('adminToken')) {
      navigate('/admin/login');
    }

    // Fetch raffle data
    axios
      .get(`http://localhost:5000/api/raffles/${id}`)
      .then((res) => {
        setRaffle(res.data);
      })
      .catch((err) => {
        setError('Failed to load raffle. Invalid secret?');
      });

    // Stop initial confetti after 3 seconds
    const timer = setTimeout(() => setShowConfetti(false), 3000);
    return () => clearTimeout(timer);
  }, [id, navigate]);

  const handleEndRaffle = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/api/raffles/end/${id}/${secret}`);
      setRaffle({ ...raffle, winner: response.data.winner, endTime: new Date() });
      setShowConfetti(true);
      setTimeout(() => {
        setShowConfetti(false);
        navigate(`/winner/${id}`);
      }, 3000);
    } catch (err) {
      setError('Failed to end raffle. Invalid secret?');
    }
  };

  if (error) return <div className="text-red-500 text-center p-8">{error}</div>;
  if (!raffle) return <div className="text-center p-8">Loading...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#6BCB77] to-[#4D96FF] p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-2xl mx-auto bg-white dark:bg-gray-800 rounded-2xl shadow-xl overflow-hidden"
      >
        <div className="bg-[#4D96FF] p-4 flex justify-between items-center">
          <img
            src="/logo-placeholder.png"
            alt="Raffle Hub Logo"
            className="h-16 w-auto"
            onError={(e) => (e.target.src = 'https://via.placeholder.com/150x60?text=Raffle+Hub')}
          />
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => navigate('/admin/create')}
            className="bg-[#FFD93D] text-black p-2 rounded-lg font-semibold hover:bg-[#FFCA28] transition-colors"
          >
            Create New Raffle
          </motion.button>
        </div>
        <div className="p-6">
          <h1 className="text-3xl font-poppins font-bold text-[#FF6B6B] mb-6 text-center">
            Manage Raffle: {raffle.title}
          </h1>
          <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-6">
            <p className="text-gray-700 dark:text-gray-200">
              <span className="font-semibold">Cash Prize:</span> GHS {raffle.cashPrize}
            </p>
            <p className="text-gray-700 dark:text-gray-200">
              <span className="font-semibold">Ticket Price:</span> GHS {raffle.ticketPrice}
            </p>
            <p className="text-gray-700 dark:text-gray-200">
              <span className="font-semibold">Ends:</span> {new Date(raffle.endTime).toLocaleString()}
            </p>
            <p className="text-gray-700 dark:text-gray-200">
              <span className="font-semibold">Participants:</span>{' '}
              <motion.span
                key={raffle.participants.length}
                initial={{ scale: 1.2 }}
                animate={{ scale: 1 }}
                transition={{ duration: 0.3 }}
                className="text-[#6BCB77] font-bold"
              >
                {raffle.participants.length}
              </motion.span>
            </p>
          </div>
          <CountdownTimer endTime={raffle.endTime} />
          <div className="mt-8">
            <h2 className="text-2xl font-poppins font-semibold text-[#4D96FF] mb-4">Participants</h2>
            <AnimatePresence>
              {raffle.participants.length > 0 ? (
                <ul className="space-y-3 max-h-64 overflow-y-auto">
                  {raffle.participants.map((p) => (
                    <motion.li
                      key={p.ticketNumber}
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      exit={{ opacity: 0, x: 20 }}
                      transition={{ duration: 0.3 }}
                      className="flex items-center p-3 bg-gray-100 dark:bg-gray-700 rounded-lg hover:bg-gray-200 dark:hover:bg-gray-600 transition-colors"
                    >
                      <div className="w-10 h-10 rounded-full bg-[#FF6B6B] flex items-center justify-center text-white font-bold mr-3">
                        {p.displayName.charAt(0).toUpperCase()}
                      </div>
                      <div>
                        <span className="text-gray-800 dark:text-gray-200">{p.displayName}</span>
                        <p className="text-sm text-gray-500 dark:text-gray-400">Ticket: {p.ticketNumber}</p>
                      </div>
                    </motion.li>
                  ))}
                </ul>
              ) : (
                <p className="text-gray-500 dark:text-gray-400">No participants yet.</p>
              )}
            </AnimatePresence>
          </div>
          {!raffle.winner && (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleEndRaffle}
              className="bg-[#FFD93D] text-black p-3 rounded-lg w-full font-semibold hover:bg-[#FFCA28] transition-colors mt-6"
            >
              End Raffle & Pick Winner
            </motion.button>
          )}
          {raffle.winner && (
            <p className="text-[#4D96FF] font-bold mt-6 text-center">
              Winner: {raffle.participants.find((p) => p.ticketNumber === raffle.winner)?.displayName}
            </p>
          )}
        </div>
      </motion.div>
      <ConfettiBurst trigger={showConfetti} />
    </div>
  );
};

export default AdminDashboard;
