import React, { useState } from 'react';
import { motion } from 'framer-motion';
import axios from 'axios';
import ConfettiBurst from '../components/ConfettiBurst';

const RaffleCreate = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    cashPrize: '',
    ticketPrice: '',
    endTime: '',
    prizeImage: null,
  });
  const [success, setSuccess] = useState(false);
  const [shareLink, setShareLink] = useState('');
  const [adminLink, setAdminLink] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, prizeImage: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      const response = await axios.post('http://localhost:5000/api/raffles/create', data, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setShareLink(response.data.shareLink);
      setAdminLink(response.data.adminLink);
      setSuccess(true);
    } catch (err) {
      console.error(err);
      alert('Error creating raffle');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#FFD93D] to-[#6BCB77] flex items-center justify-center p-4">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-8 max-w-lg w-full"
      >
        <h1 className="text-3xl font-poppins font-bold text-[#4D96FF] mb-6">Create a Raffle ðŸŽ‰</h1>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-[#FF6B6B] font-semibold">Raffle Title</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              placeholder="E.g., Win a New iPhone!"
              required
            />
          </div>
          <div>
            <label className="block text-[#FF6B6B] font-semibold">Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              placeholder="Describe the raffle"
            />
          </div>
          <div>
            <label className="block text-[#FF6B6B] font-semibold">Cash Prize (GHS, optional)</label>
            <input
              type="number"
              name="cashPrize"
              value={formData.cashPrize}
              onChange={handleInputChange}
              className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              placeholder="E.g., 1000"
            />
          </div>
          <div>
            <label className="block text-[#FF6B6B] font-semibold">Ticket Price (GHS, 0 for free)</label>
            <input
              type="number"
              name="ticketPrice"
              value={formData.ticketPrice}
              onChange={handleInputChange}
              className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              placeholder="E.g., 10"
              required
            />
          </div>
          <div>
            <label className="block text-[#FF6B6B] font-semibold">End Time</label>
            <input
              type="datetime-local"
              name="endTime"
              value={formData.endTime}
              onChange={handleInputChange}
              className="w-full p-2 border rounded dark:bg-gray-700 dark:text-white"
              required
            />
          </div>
          <div>
            <label className="block text-[#FF6B6B] font-semibold">Prize Image</label>
            <input
              type="file"
              name="prizeImage"
              onChange={handleFileChange}
              className="w-full p-2"
              accept="image/*"
            />
          </div>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full bg-[#4D96FF] text-white p-3 rounded font-semibold"
          >
            Create Raffle
          </motion.button>
        </form>
        {success && (
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="mt-4 p-4 bg-[#6BCB77] text-white rounded"
          >
            <p>Raffle Created!</p>
            <p>Share: <a href={shareLink} className="underline">{shareLink}</a></p>
            <p>Admin: <a href={adminLink} className="underline">Manage Raffle</a></p>
            <ConfettiBurst trigger={success} />
          </motion.div>
        )}
      </motion.div>
    </div>
  );
};

export default RaffleCreate;
