import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { motion } from 'framer-motion';
import LandingPage from './pages/LandingPage';
import RaffleCreate from './pages/RaffleCreate';
import RaffleDetails from './pages/RaffleDetails';
import TicketDownload from './pages/TicketDownload';
import AdminDashboard from './pages/AdminDashboard';
import WinnerAnnouncement from './components/WinnerAnnouncement';

function App() {
  return (
    <Router>
      <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
        <Routes>
          <Route path="/" element={<LandingPage />} />
          <Route path="/create" element={<RaffleCreate />} />
          <Route path="/raffle/:id" element={<RaffleDetails />} />
          <Route path="/ticket/:id" element={<TicketDownload />} />
          <Route path="/admin/:id" element={<AdminDashboard />} />
          <Route path="/winner/:id" element={<WinnerAnnouncement />} />
        </Routes>
      </motion.div>
    </Router>
  );
}

export default App;
