import React, { useState, useEffect } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { QRCodeCanvas } from 'qrcode.react';
import { motion } from 'framer-motion';
import ConfettiBurst from '../components/ConfettiBurst';
import axios from 'axios';

const TicketDownload = () => {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [ticketData, setTicketData] = useState(null);
  const [showConfetti, setShowConfetti] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRaffle = async () => {
      if (!id || id === 'undefined') {
        setError('Invalid raffle ID');
        return;
      }
      try {
        console.log(`Fetching raffle with ID: ${id}`);
        const response = await axios.get(`http://localhost:5000/api/raffles/${id}`);
        console.log('Raffle data:', response.data);
        setTicketData({ ...response.data, participants: location.state?.participant ? [location.state.participant] : response.data.participants });
        // Check if raffle has ended
        if (response.data.winner || new Date(response.data.endTime) < new Date()) {
          console.log('Raffle has ended, redirecting to /winner/', id);
          navigate(`/winner/${id}`);
        }
      } catch (err) {
        console.error('Error fetching raffle:', err.response?.data || err.message);
        setError(`Failed to load ticket: ${err.response?.data?.error || err.message}`);
      }
    };

    // Use location.state if available, otherwise fetch from backend
    const participant = location.state?.participant || null;
    if (participant && location.state?.raffle) {
      setTicketData({ ...location.state.raffle, participants: [participant] });
      // Still fetch to check if raffle has ended
      fetchRaffle();
    } else {
      fetchRaffle();
    }

    // Stop confetti after 3 seconds
    const timer = setTimeout(() => setShowConfetti(false), 3000);
    return () => clearTimeout(timer);
  }, [id, location, navigate]);

  const handleDownload = () => {
    if (!id || id === 'undefined') {
      setError('Invalid raffle ID');
      return;
    }
    axios
      .get(`http://localhost:5000/api/raffles/${id}/ticket`, { responseType: 'blob' })
      .then((response) => {
        const url = window.URL.createObjectURL(new Blob([response.data], { type: 'application/pdf' }));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'raffle-ticket.pdf');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
      })
      .catch((err) => setError(`Failed to download ticket: ${err.response?.data?.error || err.message}`));
  };

  const handleGoBack = () => {
    navigate(`/raffle/${id}`);
  };

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#FF6B6B] to-[#FFD93D] flex items-center justify-center p-4">
        <div className="text-red-500 text-center">{error}</div>
      </div>
    );
  }

  if (!ticketData) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-[#FF6B6B] to-[#FFD93D] flex items-center justify-center p-4">
        <div className="text-white text-center">Loading...</div>
      </div>
    );
  }

  const participant = ticketData.participants[ticketData.participants.length - 1];

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#FF6B6B] to-[#FFD93D] p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-md mx-auto bg-white dark:bg-gray-800 rounded-2xl shadow-xl overflow-hidden"
      >
        <div className="bg-[#4D96FF] p-4 flex justify-center items-center">
          <img
            src="/logo-placeholder.png"
            alt="Raffle Hub Logo"
            className="h-16 w-auto"
            onError={(e) => (e.target.src = '/fallback-logo.png')}
          />
        </div>
        <div className="p-6 text-center">
          <h1 className="text-3xl font-poppins font-bold text-[#4D96FF] mb-4">
            ðŸŽ‰ Ticket Secured!
          </h1>
          <p className="text-lg text-gray-700 dark:text-gray-200 mb-6">
            You've secured your ticket for <strong>{ticketData.title}</strong>!
          </p>
          <div className="my-6">
            <QRCodeCanvas
              value={`http://localhost:3000/ticket/${id}`}
              size={150}
              className="mx-auto"
            />
            <p className="mt-2 text-[#FF6B6B] font-semibold">
              Ticket Number: {participant?.ticketNumber || 'N/A'}
            </p>
            <p className="text-gray-600 dark:text-gray-200">
              Name: {participant?.displayName || 'N/A'}
            </p>
            <p className="text-gray-600 dark:text-gray-200">
              Expires: {new Date(ticketData.endTime).toLocaleString()}
            </p>
          </div>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleDownload}
            className="bg-[#FFD93D] text-black p-3 rounded-lg w-full font-semibold hover:bg-[#FFCA28] transition-colors mb-3"
          >
            Download Ticket
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleGoBack}
            className="bg-[#4D96FF] text-white p-3 rounded-lg w-full font-semibold hover:bg-[#3D86E6] transition-colors"
          >
            Go Back to Raffle
          </motion.button>
        </div>
      </motion.div>
      <ConfettiBurst trigger={showConfetti} />
    </div>
  );
};

export default TicketDownload;
