import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { QRCodeCanvas } from 'qrcode.react';
import { motion } from 'framer-motion';
import ConfettiBurst from '../components/ConfettiBurst';
import axios from 'axios';

const TicketDownload = () => {
  const { id } = useParams();
  const location = useLocation();
  const [ticketData, setTicketData] = useState(null);
  const [showConfetti, setShowConfetti] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    // Get ticket data from location.state or fetch from backend
    const participant = location.state?.participant || null;
    if (participant) {
      setTicketData({ ...location.state.raffle, participants: [participant] });
    } else {
      // Fallback: fetch raffle data
      axios
        .get(`http://localhost:5000/api/raffles/${id}`)
        .then((res) => setTicketData(res.data))
        .catch((err) => setError(`Failed to load ticket: ${err.response?.data?.error || err.message}`));
    }

    // Stop confetti after 3 seconds
    const timer = setTimeout(() => setShowConfetti(false), 3000);
    return () => clearTimeout(timer);
  }, [id, location]);

  const handleDownload = () => {
    axios
      .get(`http://localhost:5000/api/raffles/${id}/ticket`, { responseType: 'blob' })
      .then((response) => {
        const url = window.URL.createObjectURL(new Blob([response.data], { type: 'application/pdf' }));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', 'raffle-ticket.pdf');
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
      })
      .catch((err) => setError(`Failed to download ticket: ${err.response?.data?.error || err.message}`));
  };

  if (error) return <div className="text-red-500 text-center">{error}</div>;
  if (!ticketData) return <div>Loading...</div>;

  const participant = ticketData.participants[ticketData.participants.length - 1];

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#FF6B6B] to-[#FFD93D] flex items-center justify-center p-4">
      <motion.div
        initial={{ scale: 0.8, opacity: 0 }}
        animate={{ scale: 1, opacity: 1 }}
        transition={{ duration: 0.5 }}
        className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-8 max-w-md w-full text-center"
      >
        <h1 className="text-3xl font-poppins font-bold text-[#4D96FF] mb-4">
          ðŸŽ‰ Congratulations!
        </h1>
        <p className="text-lg text-gray-700 dark:text-gray-200">
          You've secured your ticket for <strong>{ticketData.title}</strong>!
        </p>
        <div className="my-6">
          <QRCodeCanvas
            value={`raffle:${id},ticket:${participant?.ticketNumber}`}
            size={150}
            className="mx-auto"
          />
          <p className="mt-2 text-[#FF6B6B]">
            Ticket Number: {participant?.ticketNumber || 'N/A'}
          </p>
          <p className="text-gray-600 dark:text-gray-200">
            Name: {participant?.displayName || 'N/A'}
          </p>
          <p className="text-gray-600 dark:text-gray-200">
            Expires: {new Date(ticketData.endTime).toLocaleString()}
          </p>
        </div>
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={handleDownload}
          className="bg-[#6BCB77] text-white p-3 rounded font-semibold"
        >
          Download Ticket PDF
        </motion.button>
        <ConfettiBurst trigger={showConfetti} />
      </motion.div>
    </div>
  );
};

export default TicketDownload;
