import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';
import ConfettiBurst from '../components/ConfettiBurst';

const WinnerAnnouncement = () => {
  const { id } = useParams();
  const [raffle, setRaffle] = useState(null);

  useEffect(() => {
    axios.get(`/api/raffles/${id}`).then((res) => {
      setRaffle(res.data);
    });
  }, [id]);

  if (!raffle) return <div>Loading...</div>;

  const winner = raffle.participants.find(p => p.ticketNumber === raffle.winner);

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#4D96FF] to-[#FF6B6B] flex items-center justify-center p-4">
      <motion.div
        initial={{ scale: 0.5, rotate: -10 }}
        animate={{ scale: 1, rotate: 0 }}
        transition={{ duration: 0.8, type: 'spring' }}
        className="text-center"
      >
        <h1 className="text-5xl font-poppins font-bold text-white mb-6">
          üèÜ Winner Announced! üèÜ
        </h1>
        <motion.div
          animate={{ scale: [1, 1.1, 1], rotate: [0, 5, -5, 0] }}
          transition={{ repeat: Infinity, duration: 2 }}
          className="bg-white dark:bg-gray-800 rounded-lg p-8 shadow-lg"
        >
          <p className="text-3xl text-[#FFD93D] font-semibold">
            {winner ? `${winner.displayName} wins!` : 'No winner yet.'}
          </p>
          <p className="text-lg text-gray-600 dark:text-gray-200">
            Raffle: {raffle.title}
          </p>
        </motion.div>
        <ConfettiBurst trigger={true} />
      </motion.div>
    </div>
  );
};

export default WinnerAnnouncement;
