import { motion } from "framer-motion";
import { Link } from "react-router-dom";
import ConfettiBurst from "../components/ConfettiBurst";
import { useState } from "react";

const LandingPage = () => {
  const [confetti, setConfetti] = useState(false);

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#FFD93D] to-[#6BCB77] flex flex-col items-center justify-center">
      <motion.h1
        initial={{ y: -50, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8 }}
        className="text-5xl font-poppins text-[#4D96FF] space-x-6"
        onHoverStart={() => setConfetti(true)}
      >
        <Link
          to="/create"
          className="hover:text-[#FF6B6B] transition duration-300 underline"
        >
          üéüÔ∏è Start a Raffle
        </Link>
        <span className="mx-3">|</span>
        <Link
          to="/raffle"
          className="hover:text-[#FF6B6B] transition duration-300 underline"
        >
          üéâ Join a Raffle
        </Link>
      </motion.h1>
      <ConfettiBurst trigger={confetti} />
    </div>
  );
};

export default LandingPage;
