import { useParams, useNavigate } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const TicketDownload = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [error, setError] = useState('');

  useEffect(() => {
    // Ticket is already downloaded via verifyPayment; this page confirms or redirects
    setTimeout(() => {
      navigate(`/raffle/${id}`);
    }, 3000); // Redirect after 3 seconds
  }, [id, navigate]);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="p-8 bg-white rounded-lg shadow-lg max-w-md mx-auto text-center"
    >
      {error ? (
        <div className="text-red-500">{error}</div>
      ) : (
        <>
          <h1 className="text-2xl font-poppins font-bold text-[#FF6B6B] mb-4">Ticket Purchased!</h1>
          <p>Your ticket has been downloaded. Check your downloads folder for <b>ticket.pdf</b>.</p>
          <p>Redirecting to raffle page in 3 seconds...</p>
          <button
            onClick={() => navigate(`/raffle/${id}`)}
            className="mt-4 bg-[#4D96FF] text-white p-2 rounded"
          >
            Back to Raffle
          </button>
        </>
      )}
    </motion.div>
  );
};

export default TicketDownload;
