import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion';

const CreateRaffle = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    prizeTypes: ['cash'],
    cashPrize: '',
    itemName: '',
    ticketPrice: '',
    endTime: '',
    prizeImage: null,
  });
  const [error, setError] = useState('');

  const generateCreatorSecret = () => {
    return Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
  };

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      setFormData((prev) => ({
        ...prev,
        prizeTypes: checked
          ? [...prev.prizeTypes, value]
          : prev.prizeTypes.filter((type) => type !== value),
      }));
    } else if (type === 'file') {
      setFormData((prev) => ({ ...prev, prizeImage: files[0] }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const token = localStorage.getItem('adminToken');
    if (!token) {
      setError('Admin login required');
      return;
    }

    const data = new FormData();
    data.append('title', formData.title);
    data.append('description', formData.description);
    data.append('prizeTypes', JSON.stringify(formData.prizeTypes));
    data.append('cashPrize', formData.cashPrize);
    data.append('itemName', formData.itemName);
    data.append('ticketPrice', formData.ticketPrice);
    data.append('endTime', new Date(formData.endTime).toISOString());
    data.append('creatorSecret', generateCreatorSecret());
    if (formData.prizeImage) {
      data.append('prizeImage', formData.prizeImage);
    }

    try {
      const response = await axios.post(
        'https://raffle-backend-rho.vercel.app/api/raffles/create',
        data,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      navigate(`/admin/${response.data._id}?secret=${response.data.creatorSecret}`);
    } catch (err) {
      setError(`Failed to create raffle: ${err.response?.data?.error || err.message}`);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#6BCB77] to-[#4D96FF] p-6">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="max-w-md mx-auto bg-white dark:bg-gray-800 rounded-2xl shadow-xl p-6"
      >
        <h1 className="text-3xl font-poppins font-bold text-[#FF6B6B] mb-6 text-center">
          Create New Raffle
        </h1>
        {error && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="mb-4 p-4 bg-red-600 text-white rounded-lg text-center"
          >
            {error}
          </motion.div>
        )}
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Title</label>
            <input
              type="text"
              name="title"
              value={formData.title}
              onChange={handleChange}
              required
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              required
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Prize Types</label>
            <div className="flex space-x-4">
              <label className="flex items-center">
                <input
                  type="checkbox"
                  name="prizeTypes"
                  value="cash"
                  checked={formData.prizeTypes.includes('cash')}
                  onChange={handleChange}
                  className="mr-2"
                />
                Cash
              </label>
              <label className="flex items-center">
                <input
                  type="checkbox"
                  name="prizeTypes"
                  value="item"
                  checked={formData.prizeTypes.includes('item')}
                  onChange={handleChange}
                  className="mr-2"
                />
                Item
              </label>
            </div>
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Cash Prize (GHS)</label>
            <input
              type="number"
              name="cashPrize"
              value={formData.cashPrize}
              onChange={handleChange}
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Item Name (if applicable)</label>
            <input
              type="text"
              name="itemName"
              value={formData.itemName}
              onChange={handleChange}
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Ticket Price (GHS)</label>
            <input
              type="number"
              name="ticketPrice"
              value={formData.ticketPrice}
              onChange={handleChange}
              required
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">End Time</label>
            <input
              type="datetime-local"
              name="endTime"
              value={formData.endTime}
              onChange={handleChange}
              required
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <div>
            <label className="block text-gray-700 dark:text-gray-200 font-semibold">Prize Image (optional)</label>
            <input
              type="file"
              name="prizeImage"
              accept="image/*"
              onChange={handleChange}
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-600 dark:bg-gray-700 dark:text-gray-200"
            />
          </div>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="submit"
            className="w-full bg-[#FFD93D] text-black p-3 rounded-lg font-semibold hover:bg-[#FFCA28] transition-colors"
          >
            Create Raffle
          </motion.button>
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            type="button"
            onClick={() => navigate('/admin')}
            className="w-full bg-gray-600 text-white p-3 rounded-lg font-semibold hover:bg-gray-700 transition-colors mt-4"
          >
            Back to Dashboard
          </motion.button>
        </form>
      </motion.div>
    </div>
  );
};

export default CreateRaffle;
