import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import axios from 'axios';
import CountdownTimer from '../components/CountdownTimer';
import { motion } from 'framer-motion';

const AdminDashboard = () => {
  const { id } = useParams();
  const location = useLocation();
  const secret = new URLSearchParams(location.search).get('secret');
  const [raffle, setRaffle] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`/api/raffles/${id}`).then((res) => {
      setRaffle(res.data);
    }).catch((err) => {
      setError('Failed to load raffle. Invalid secret?');
    });
  }, [id]);

  const handleEndRaffle = async () => {
    try {
      const response = await axios.post(`/api/raffles/end/${id}/${secret}`);
      setRaffle({ ...raffle, winner: response.data.winner, endTime: new Date() });
      window.location.href = `/winner/${id}`;
    } catch (err) {
      setError('Failed to end raffle. Invalid secret?');
    }
  };

  if (error) return <div className="text-red-500 text-center p-4">{error}</div>;
  if (!raffle) return <div>Loading...</div>;

  return (
    <div className="min-h-screen bg-gradient-to-br from-[#6BCB77] to-[#4D96FF] flex items-center justify-center p-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="bg-white dark:bg-gray-800 rounded-lg shadow-lg p-8 max-w-2xl w-full"
      >
        <h1 className="text-3xl font-poppins font-bold text-[#FF6B6B] mb-6">
          Manage Raffle: {raffle.title}
        </h1>
        <CountdownTimer endTime={raffle.endTime} />
        <p className="text-lg text-gray-700 dark:text-gray-200 mb-4">
          Participants: {raffle.participants.length}
        </p>
        <ul className="space-y-2 mb-6">
          {raffle.participants.map((p) => (
            <li key={p.ticketNumber} className="p-2 bg-gray-100 dark:bg-gray-700 rounded">
              {p.displayName} - Ticket: {p.ticketNumber}
            </li>
          ))}
        </ul>
        {!raffle.winner && (
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleEndRaffle}
            className="bg-[#FFD93D] text-black p-3 rounded font-semibold"
          >
            End Raffle & Pick Winner
          </motion.button>
        )}
        {raffle.winner && (
          <p className="text-[#4D96FF] font-bold">
            Winner: {raffle.participants.find(p => p.ticketNumber === raffle.winner)?.displayName}
          </p>
        )}
      </motion.div>
    </div>
  );
};

export default AdminDashboard;
